import { useEffect, useState } from "react";
import { io } from "socket.io-client";

/**
 *
 * @param {{hostname: string, architecture: string, platform: string,uptime: string,cpu:string,memory: string,network: string,date: string}} props
 */
function Report(props) {
  const port = 5782;
  const INTERVAL = 2000;
  var [status, setStatus] = useState(null);
  const socket = io.connect(props.IP + ":" + port);
  useEffect(() => {
    socket.emit("get_init");
    socket.on("send_data", (data) => {
      setStatus(data);
      console.log(data);
      setInterval(() => {
        console.log("querying data for", props.IP);
        socket.emit("get_status",(status));
        socket.on("send_data", (newData) => {
          setStatus(newData);
        });
      }, INTERVAL);
    });
    socket.on("error", () => {
        console.log("error");
    });
  }, []);
