import { useEffect, useState } from "react";
import { io } from "socket.io-client";
import classes from "./Report.module.css";

/**
 *
 * @param {{hostname: string, architecture: string, platform: string,uptime: string,cpu:string,memory: string,network: string,date: string}} props
 */
function Report(props) {
  const port = 5782;
  const INTERVAL = 2000;
  var [status, setStatus] = useState(null);

  useEffect(() => {
    // const socket = io.connect(props.IP + ":" + port);
    const socket = io.connect("http://localhost:5782");

    socket.emit("get_init");
    socket.on("send_data", (data) => {
      socket.close();
      setStatus(data);
      getStatus(data);
    });
    socket.on("error", () => {
      console.log("error");
    });
  }, []);

  function getStatus(data) {
    console.log(data);
    const socket = io.connect(props.IP + ":" + port);
    socket.emit("get_status", data);
    socket.on("send_data", (data) => {
      setStatus(data);
      setTimeout(() => {
        getStatus(data);
      }, INTERVAL);
      socket.close();
    });
  }

  if (status === null) {
    return (
      <div>
        <h1>Loading...</h1>
      </div>
    );
  }
  return (
    <div>
      <div className={classes.grid}>
        <div className={classes.smallInfo}>
          <b>Architecture:</b> {status.architecture}
          <br />
          <b>Platform:</b> {status.platform}
          <br />
          <b>Up Time:</b> {status.uptime} seconds
          <br />
          <b>Memory:</b> {(status.memory.used / 1000).toFixed(1)}/
          {(status.memory.total / 1000).toFixed(1)} GB
        </div>
        <div className={classes.smallInfo}>
          Network: {JSON.stringify(status.network)}
        </div>
      </div>
      <h1>Host Name: {status.hostname}</h1>
      <ul>
        <li>
          CPU: {status.cpu[0].model}
          <ul>
            {status.cpu.map((proc, idx) => {
              return (
                <li key={idx}>
                  {proc.model} ({proc.speed}): {JSON.stringify(proc.usage)}
                </li>
              );
            })}
          </ul>
        </li>
      </ul>
    </div>
  );
}

export default Report;
