import { useEffect, useState } from "react";
import { io } from "socket.io-client";

/**
 *
 * @param {{hostname: string, architecture: string, platform: string,uptime: string,cpu:string,memory: string,network: string,date: string}} props
 */
function Report(props) {
  const port = 5782;
  const INTERVAL = 2000;
  var [status, setStatus] = useState(null);
  useEffect(() => {
    const socket = io.connect(props.IP + ":" + port);
    socket.emit("get_init");
    socket.on("send_data", (data) => {
      setStatus(data);
      console.log(data);
      socket.close();
      useEffect(() => {
        const interval = setInterval(() => {
          const socket = io.connect(props.IP + ":" + port);
          socket.emit("get_status",(status));
          socket.on("send_data", (data) => {
            setStatus(data);
            socket.close();
          });
        }, INTERVAL);
        return () => clearInterval(interval);
      }, []);
    });
    socket.on("error", () => {
      //will be handled later...
    });
  }, []);
  return (
    <div>
      {/* <h1>Host Name: {props.hostname}</h1>
